// Anti-Spam
function strrev(str) {
if (!str) return '';
var revstr = '';
for (var i = str.length-1; i>=0; i--)
revstr += str.charAt(i)
return revstr;
}
var array = new Array('gro', '.', 'woteub', '@', 'luap');
for (var i = array.length - 1; i >= 0; --i)  {
document.write(strrev(array[i]));
}

