content.code:
# This code
my @baz = @foo >>+<< @bar;

# Is the same as:
loop ($i = 0; $i < @bar.elems; ++$i) {
	push @baz, @foo[$i] + @bar[$i];
}

# This code
my @baz = @foo >>>>+<<<< @bar;

# Is the same as:
loop ($i = 0; $i < @bar.elems; ++$i) {
	my $foo = @foo[$i];
	my $bar = @bar[$i];
	my $baz = @baz[$i];
	loop ($j = 0; $j < $bar->elems; ++$j) {
		push @$baz, $foo->[$j] + $bar->[$j];
	}
}

# This code
my @baz = @foo >>>>>>+<<<<<< @bar;

# Is the same as:
loop ($i = 0; $i <&t; @bar.elems; ++$i) {
	my $foo = @foo[$i];
	my $bar = @bar[$i];
	my $baz = @baz[$i];
	loop ($j = 0; $j < $bar->elems; ++$j) {
		my $foo_ = $foo->[$j];
		my $bar_ = $bar->[$j];
		my $baz_ = $baz->[$j];
		loop ($k = 0; $k < $bar_->elems; ++$k) {
			push @$baz_, $foo_->[$k] + $bar_->[$k];
		}
	}
}
